@App:name("apim_alert_frequent_tier_limit_hitting_0")
@App:description("Alerts when a user or application goes beyond the subscribed quota frequently")

@source(type = 'inMemory' , topic = 'ThrottledOutStream')
define stream ThrottledOutStream(
 meta_clientType string,
 username string,
 userTenantDomain string,
 apiName string,
 apiVersion string,
 apiContext string,
 apiCreator string,
 apiCreatorTenantDomain string,
 applicationId string,
 applicationName string,
 subscriber string,
 throttledOutReason string,
 gatewayType string,
 throttledOutTimestamp long,
 hostname string
);

@sink(type = 'inMemory', topic = 'TierLimitHittingAlertStream')
define stream TierLimitHittingAlertStream(subscriber string, apiCreator string, apiName string, applicationId string, applicationName string, apiCreatorTenantDomain string, userTenantDomain string, message string, severity int, alertTimestamp long);

define stream SuppressedTemporarySubscriberStream(apiCreator string, apiName string, apiVersion string, applicationId string, applicationName string, apiCreatorTenantDomain string, userTenantDomain string, subscriber string, numHits long);

define stream SuppressedTemporaryUserStream(username string, apiCreator string, apiName string, apiVersion string, applicationId string, applicationName string, apiCreatorTenantDomain string, userTenantDomain string, subscriber string, numHits long);

--checks whether total throttled out count with the throttled out reson SUBSCRIPTION_LIMIT_EXCEEDED is higer than the specified value
@info(name = 'TemporarySubscriberStream generating Query: 10 min batch')
from ThrottledOutStream[throttledOutReason == 'SUBSCRIPTION_LIMIT_EXCEEDED']#window.timeBatch(1 min)
select apiCreator, apiName, apiVersion, applicationId, applicationName, apiCreatorTenantDomain, userTenantDomain, subscriber, count() as numHits
 	group by apiCreator, apiName, apiVersion, applicationId
insert into TemporaryMinWindowSubscriberStream;

@info(name = 'TemporarySubscriberStream generating Query')
from TemporaryMinWindowSubscriberStream#window.time(${countingTime})
select apiCreator, apiName, apiVersion, applicationId, applicationName, apiCreatorTenantDomain, userTenantDomain, subscriber, sum(numHits) as numHits
 	group by apiCreator, apiName, apiVersion, applicationId
 	having numHits > ${numOfTierCrossing}
insert into TemporarySubscriberStream;

--checks whether total throttled out count with the throttled out reson APPLICATION_LIMIT_EXCEEDED is higer than the specified value
@info(name = 'TemporaryUserStream generating Query: 10 min window')
from ThrottledOutStream[throttledOutReason == 'APPLICATION_LIMIT_EXCEEDED']#window.timeBatch(1 min)
select username, apiCreator, apiName, apiVersion, applicationId, applicationName,
apiCreatorTenantDomain, userTenantDomain, subscriber, count() as numHits
 	group by username, apiName, apiVersion, applicationId
insert into TemporaryMinwindowUserStream;

--checks whether total throttled out count with the throttled out reson APPLICATION_LIMIT_EXCEEDED is higer than the specified value
@info(name = 'TemporaryUserStream generating Query')
from TemporaryMinwindowUserStream#window.time(${countingTime})
select username, apiCreator, apiName, apiVersion, applicationId, applicationName, apiCreatorTenantDomain, userTenantDomain, subscriber, sum(numHits) as numHits
 	group by username, apiName, apiVersion, applicationId
 	having numHits > ${numOfTierCrossing}
insert into TemporaryUserStream;

--this query ensures that the same type of alert is not generated for same apiCreator again for a time defined in #window.time(),
@info(name = 'Query contains Suppression logic')
from TemporarySubscriberStream#window.length(1) as a left outer join SuppressedTemporarySubscriberStream#window.time(${alertSuppressionTime} minutes) as b
 	on (a.apiCreator == b.apiCreator and a.apiVersion == b.apiVersion and a.applicationId == b.applicationId)
select a.apiCreator, a.apiName, a.apiVersion, a.applicationId, a.applicationName, a.apiCreatorTenantDomain, a.userTenantDomain, a.subscriber, a.numHits
 	having b.apiCreator is null
insert into SuppressedTemporarySubscriberStream ;

--this query ensures that the same type of alert is not generated for same user again for a time defined in #window.time()
@info(name = 'Alert Suppression')
from TemporaryUserStream#window.length(1) as a left outer join SuppressedTemporaryUserStream#window.time(${alertSuppressionTime} minutes) as b
 	on (a.username == b.username and a.apiVersion == b.apiVersion and a.applicationId == b.applicationId)
select a.username, a.apiCreator, a.apiName, a.apiVersion, a.applicationId, a.applicationName, a.apiCreatorTenantDomain, a.userTenantDomain, a.subscriber, a.numHits
 	having b.username is null
insert into SuppressedTemporaryUserStream;

@info(name = 'Query generates TierLimitHittingAlertStream')
from SuppressedTemporarySubscriberStream
select ifThenElse(userTenantDomain == 'carbon.super', str:concat(subscriber, "@carbon.super"), subscriber) as subscriber, ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiName, applicationId, applicationName, apiCreatorTenantDomain, userTenantDomain, str:concat(applicationName, " Application frequently goes beyond the allocated quota when accessing ", apiName, ' ', apiVersion) as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into TierLimitHittingAlertStream;

@info(name = 'Query generates TierLimitHittingAlertStream from supressedUserStream')
from SuppressedTemporaryUserStream
select ifThenElse(userTenantDomain == 'carbon.super', str:concat(subscriber, "@carbon.super"), subscriber) as subscriber, ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiName, applicationId, applicationName, apiCreatorTenantDomain, userTenantDomain, str:concat("User ", username, " frequently crosses the limit set.") as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into TierLimitHittingAlertStream;

@App:name("apim_alert_health_availability_0")
@App:description("Determines the availablity and health of an api")

define trigger LoadInMemoryTable at 'start';

@source(type = 'inMemory' , topic = 'APIM_REQUEST')
define stream Request (meta_clientType string, applicationConsumerKey string, applicationName string, applicationId string, applicationOwner string, apiContext string, apiName string, apiVersion string, apiResourcePath string, apiResourceTemplate string, apiMethod string, apiCreator string, apiCreatorTenantDomain string, apiTier string, apiHostname string, username string, userTenantDomain string, userIp string, userAgent string, requestTimestamp long, throttledOut bool, responseTime long, serviceTime long, backendTime long, responseCacheHit bool, responseSize long, protocol string, responseCode int, destination string, securityLatency long, throttlingLatency long, requestMedLat long, responseMedLat long, backendLatency long, otherLatency long, gatewayType string, label string);

@source(type = 'inMemory', topic = 'ApiCreatorAlertConfigurationStream' )
define stream ApiCreatorAlertConfigurationStream (
 apiName string,
 apiVersion string,
 apiCreator string,
 apiCreatorTenantDomain string,
 thresholdResponseTime long,
 thresholdBackendTime long);

define stream SuppressedResponseTimeAlertStream(apiName string, apiVersion string, apiContext string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long);

define stream SuppressedResponseCodeAlertStream(apiName string, apiVersion string, apiContext string, apiCreator string, apiCreatorTenantDomain string);

@sink(type = 'inMemory', topic = 'ApimApiHealthMonitorAlertStream')
define stream ApiHealthMonitorAlertStream(apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, message string, severity int, alertTimestamp long);

@store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB')
@PrimaryKey('apiName', 'apiVersion', 'apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfiguration (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long, thresholdBackendTime long);

@PrimaryKey('apiName', 'apiVersion', 'apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfigurationInMemoryTable (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long);

@info(name = 'creatorConfigurationInfoRetrievQuery')
from Request as R join ApiCreatorAlertConfigurationInMemoryTable as C
 	on(R.apiName == C.apiName and R.apiVersion == C.apiVersion and R.apiCreator == C.apiCreator and R.apiCreatorTenantDomain == C.apiCreatorTenantDomain )
select R.apiContext, R.apiName, R.apiVersion, R.apiResourceTemplate, R.apiMethod, R.apiCreator, R.apiCreatorTenantDomain, R.requestTimestamp, R.responseTime, R.backendTime, C.thresholdResponseTime, R.responseCode
insert into RequestAlertInfoStream;

partition with (apiContext of RequestAlertInfoStream)
begin
 -- checks whether the response time is higher than the threshold response time continously for 5 times
 	@info(name = 'Abnormal response time pattern check')
 	from every e1 = RequestAlertInfoStream[responseTime > thresholdResponseTime],e2 = RequestAlertInfoStream[responseTime > thresholdResponseTime],e3 = RequestAlertInfoStream[responseTime > thresholdResponseTime], e4 = RequestAlertInfoStream[responseTime > thresholdResponseTime],e5 = RequestAlertInfoStream[responseTime > thresholdResponseTime]
 	select e1.apiName, e1.apiVersion, e1.apiContext, e1.apiCreator, e1.apiCreatorTenantDomain, e1.thresholdResponseTime
 	insert into ResponseTimeAlertStream;

 --checks whether the response code is between 500 - 600 to identify a server error
 	@info(name = 'Abnormal response code pattern check')
 	from every e1 = Request[responseCode >= 500 and responseCode < 600],e2 = Request[responseCode >= 500 and responseCode < 600],e3 = Request[responseCode >= 500 and responseCode < 600], e4 = Request[responseCode >= 500 and responseCode < 600],e5 = Request[responseCode >= 500 and responseCode < 600]
 	select e1.apiName, e1.apiVersion, e1.apiContext, e1.apiCreator, e1.apiCreatorTenantDomain
 	insert into ResponseCodeAlertStream;

end;

@info(name = 'ResponseTime Alert Suppression')
from ResponseTimeAlertStream#window.length(1) as a left outer join SuppressedResponseTimeAlertStream#window.time(${alertSuppressionTime} minutes) as b
 	on a.apiName == b.apiName and a.apiVersion == b.apiVersion and a.apiCreator == b.apiCreator and a.apiCreatorTenantDomain == b.apiCreatorTenantDomain
select a.apiName, a.apiVersion, a.apiContext, a.apiCreator, a.apiCreatorTenantDomain, a.thresholdResponseTime
 	having b.apiName is null
insert into SuppressedResponseTimeAlertStream;

@info(name = 'ResponseCode Alert Suppression')
from ResponseCodeAlertStream#window.length(1) as a left outer join SuppressedResponseCodeAlertStream#window.time(${alertSuppressionTime} minutes) as b
 	on a.apiName == b.apiName and a.apiVersion == b.apiVersion and a.apiCreator == b.apiCreator and a.apiCreatorTenantDomain == b.apiCreatorTenantDomain
select a.apiName, a.apiVersion, a.apiContext, a.apiCreator, a.apiCreatorTenantDomain
 	having b.apiName is null
insert into SuppressedResponseCodeAlertStream;

@info(name = 'Query generates ApiHealthMonitorAlertStreamTemp with message for high response time alert')
from SuppressedResponseTimeAlertStream
select apiName, apiVersion, ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiCreatorTenantDomain, str:concat('Response time is higher than ' , convert(thresholdResponseTime, 'string'), ' ms continuously for 5 or more responses.') as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into ApiHealthMonitorAlertStream;

@info(name = 'Query generates ApiHealthMonitorAlertStreamTemp with message for server error alert')
from SuppressedResponseCodeAlertStream
select apiName, apiVersion, ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiCreatorTenantDomain, 'Server error occurred continuously for 5 or more times.' as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into ApiHealthMonitorAlertStream;

-- Update the AlertCreatorConfig Table @ start
@info(name = 'Load ApiCreatorAlertConfigurationInMemoryTable')
from LoadInMemoryTable join ApiCreatorAlertConfiguration
on thresholdResponseTime > 0
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
update or
insert into ApiCreatorAlertConfigurationInMemoryTable
set ApiCreatorAlertConfiguration.thresholdResponseTime = thresholdResponseTime
 	on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

-- Update AlertCreatorConfig table if receive event form APIM
@info(name = 'Update ApiCreatorAlertConfigurationInMemoryTable')
from ApiCreatorAlertConfigurationStream[thresholdResponseTime > 0]
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
update or
insert into ApiCreatorAlertConfigurationInMemoryTable
set ApiCreatorAlertConfiguration.thresholdResponseTime = thresholdResponseTime
 	on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

@info(name='DeleteConfigurationif updated value is zero')
from ApiCreatorAlertConfigurationStream[thresholdResponseTime == 0]
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
delete  ApiCreatorAlertConfigurationInMemoryTable
 	on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

@App:name("apim_alert_unusual_ip_access_0")
@App:description("Alerts if an access from a strange ip is detected")

@source(type = 'inMemory' , topic = 'APIM_REQUEST')
define stream Request (meta_clientType string, applicationConsumerKey string, applicationName string, applicationId string, applicationOwner string, apiContext string, apiName string, apiVersion string, apiResourcePath string, apiResourceTemplate string, apiMethod string, apiCreator string, apiCreatorTenantDomain string, apiTier string, apiHostname string, username string, userTenantDomain string, userIp string, userAgent string, requestTimestamp long, throttledOut bool, responseTime long, serviceTime long, backendTime long, responseCacheHit bool, responseSize long, protocol string, responseCode int, destination string, securityLatency long, throttlingLatency long, requestMedLat long, responseMedLat long, backendLatency long, otherLatency long, gatewayType string, label string);

@sink(type = 'inMemory' , topic = 'IpAccessAbnormalityAlertStream')
define stream IpAccessAbnormalityAlertStream(type string, message string, severity int, ip string, applicationName string, applicationOwner string, username string, tenantDomain string, requestTimestamp long, alertTimestamp long);

@Async(buffer.size = '32768', workers = '1', batch.size.max = '1024')
define stream ApimIPAccessSummaryUpdateStream(username string, applicationConsumerKey string, ip string, lastAccessedDate long);

@Async(buffer.size = '32768', workers = '1', batch.size.max = '1024')
define stream ApimIPAccessAlertCountUpdateStream(username string, applicationConsumerKey string, requestCount long);

@store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB')
@PrimaryKey('applicationConsumerKey', 'ip')
@index('applicationConsumerKey', 'ip', 'username')
define table ApimIPAccessSummary (username string, applicationConsumerKey string, ip string, lastAccessedDate long) ;

@store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB')
@PrimaryKey('applicationConsumerKey', 'username')
define table ApimIPAccessAlertCount (username string, applicationConsumerKey string, requestCount long);

@info(name = 'Query generates TempStream')
from Request
select applicationConsumerKey, applicationOwner, applicationName, username, userTenantDomain, userIp, requestTimestamp
 	group by applicationConsumerKey, username, userIp
 	output last every 1 minute
insert into RequestBufferStream;

@info(name='Join with ApimIPAccessAlertCount')
from RequestBufferStream#window.length(1) as R join ApimIPAccessAlertCount as T
 	on (R.applicationConsumerKey == T.applicationConsumerKey and R.username == T.username)
select R.applicationConsumerKey, R.applicationOwner, R.applicationName, R.username, R.userTenantDomain, R.userIp as ip, requestCount, requestTimestamp
insert into TempStream;

@info(name='Filterout new users')
from TempStream[requestCount > ${minReqCountPerUser}]
select applicationConsumerKey, applicationOwner, applicationName, username, userTenantDomain, ip, requestTimestamp
insert into TempStream2;

@info(name = 'Query generates ExistingUserStream')
from TempStream2 as ts left outer join ApimIPAccessSummary as iaac
 	on ts.username == iaac.username and ts.applicationConsumerKey == iaac.applicationConsumerKey and ts.ip == iaac.ip
select ts.applicationConsumerKey, ts.applicationName , ts.applicationOwner, ts.username, ts.userTenantDomain as tenantDomain, ts.ip as requestIp, iaac.ip as ip, ts.requestTimestamp, (requestTimestamp - lastAccessedDate) as timeBetweenAccess
insert into ExistingUserStream;

--If a new ip is detected from the particular username and applicationConsumerKey Note: ip will be null if its a new ip from the user
@info(name = 'query generates NewIPDetectedStream')
from ExistingUserStream[ip is null]
select "UnusualIPAccess" as type, "A request from a new IP detected." as message, ${severityLevel} as severity, requestIp as ip, applicationName, ifThenElse(tenantDomain == 'carbon.super', str:concat(applicationOwner, "@carbon.super"), applicationOwner) as applicationOwner, username, tenantDomain, requestTimestamp, (time:timestampInMilliseconds()) as alertTimestamp
insert into IpAccessAbnormalityAlertStream;

-- If IP is already used Check if the request for the IP is received after the threshold time gap
@info(name = 'query generates CheckAbnormalTimeBetweenAccessStream ')
from ExistingUserStream[(not (ip is null)) and timeBetweenAccess > ${diffOFLastAccessTimePerIP} days ]
select "UnusualIPAccess" as type, "A request from an old IP detected." as message, ${severityLevel} as severity, requestIp as ip, applicationName, ifThenElse(tenantDomain == 'carbon.super', str:concat(applicationOwner, "@carbon.super"), applicationOwner) as applicationOwner, username, tenantDomain , requestTimestamp, (time:timestampInMilliseconds()) as alertTimestamp
insert into IpAccessAbnormalityAlertStream;

@info(name = 'Batch Events ApimIPAccessSummaryUpdateStream')
from Request
select username, applicationConsumerKey, userIp as ip, requestTimestamp as lastAccessedDate
 	group by applicationConsumerKey, apiHostname, username
 	output last every 10 minutes
insert into ApimIPAccessSummaryUpdateStream;

@info(name = 'Update/Insert ApimIPAccessSummaryUpdate')
from ApimIPAccessSummaryUpdateStream
select *
update or
insert into ApimIPAccessSummary
set ApimIPAccesSummary.lastAccessedDate = lastAccessedDate, ApimIPAccessSummary.username = username
 	on ApimIPAccessSummary.applicationConsumerKey == applicationConsumerKey and ApimIPAccessSummary.ip == ip;

@info(name = 'Batch Events ApimIPAccessAlertCountUpdateStream')
from Request
select username, applicationConsumerKey, count() as requestCount
 	group by applicationConsumerKey, username
 	output last every 10 minutes
insert into ApimIPAccessAlertCountUpdateStream;

@info(name = 'Update/Insert ApimIPAccessAlertCount')
from ApimIPAccessAlertCountUpdateStream
select *
update or
insert into ApimIPAccessAlertCount
set ApimIPAccessAlertCount.requestCount = ApimIPAccessAlertCount.requestCount + requestCount
 	on ApimIPAccessAlertCount.applicationConsumerKey == applicationConsumerKey and ApimIPAccessAlertCount.username == username;

@App:name("apim_alert_abnormal_response_time_0")
@App:description("Identifies the API requests with abnormal response time and add to AllAlertStream and AbnormalResponseTimeAlertStream")

define trigger LoadInMemoryTable at 'start';

@source(type='inMemory' , topic='APIM_REQUEST')
define stream Request (meta_clientType string, applicationConsumerKey string, applicationName string, applicationId string, applicationOwner string, apiContext string,apiName string, apiVersion string, apiResourcePath string, apiResourceTemplate string, apiMethod string, apiCreator string, apiCreatorTenantDomain string, apiTier string, apiHostname string, username string, userTenantDomain string, userIp string, userAgent string, requestTimestamp long, throttledOut bool, responseTime long, serviceTime long, backendTime long, responseCacheHit bool, responseSize long, protocol string, responseCode int, destination string, securityLatency long, throttlingLatency long, requestMedLat long, responseMedLat long, backendLatency long, otherLatency long, gatewayType string, label string);

@source(type = 'inMemory', topic='ApiCreatorAlertConfigurationStream' )
define stream ApiCreatorAlertConfigurationStream (
    apiName string,
    apiVersion string,
    apiCreator string,
    apiCreatorTenantDomain string,
    thresholdResponseTime long,
    thresholdBackendTime long);

define stream SuppressedAbnormalResponseTimeAlertStream(apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, apiResourceTemplate string, apiMethod string, responseTime long, thresholdResponseTime long);

@sink(type='inMemory' , topic='AbnormalResponseTimeAlertStream')
define stream AbnormalResponseTimeAlertStream( apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, apiResourceTemplate string, apiMethod string, responseTime long, thresholdResponseTime long, message string, severity int,alertTimestamp long);

@store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB')
@PrimaryKey('apiName', 'apiVersion','apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfiguration (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long, thresholdBackendTime long);

@PrimaryKey('apiName', 'apiVersion','apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfigurationInMemoryTable (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long);

@info(name = 'creatorConfigurationInfoRetrievQuery')
from Request as R join ApiCreatorAlertConfigurationInMemoryTable as C
on (R.apiName == C.apiName and R.apiVersion == C.apiVersion and R.apiCreator== C.apiCreator and R.apiCreatorTenantDomain == C.apiCreatorTenantDomain)
select R.apiName, R.apiVersion,  R.apiCreator, R.apiCreatorTenantDomain, R.apiResourceTemplate, R.apiMethod, R.responseTime, thresholdResponseTime
insert into AbnormalResponseTimeAlertStreamTemp;

@info(name='FilterForAlerting')
from AbnormalResponseTimeAlertStreamTemp[responseTime > thresholdResponseTime ]
select *
insert into AbnormalResponseTimeAlertStreamTemp2;

@info(name = 'Alert Suppression')
from AbnormalResponseTimeAlertStreamTemp2#window.length(1) as a left outer join SuppressedAbnormalResponseTimeAlertStream#window.time(${alertSuppressionTime} minutes) as b
on (a.apiName == b.apiName and a.apiVersion== b.apiVersion and a.apiCreator== b.apiCreator and a.apiCreatorTenantDomain==b.apiCreatorTenantDomain and
    a.apiResourceTemplate==b.apiResourceTemplate and a.apiMethod== b.apiMethod)
select a.apiName, a.apiVersion, a.apiCreator, a.apiCreatorTenantDomain, a.apiResourceTemplate, a.apiMethod, a.responseTime, a.thresholdResponseTime
having b.apiName  is null
insert into SuppressedAbnormalResponseTimeAlertStream;

from SuppressedAbnormalResponseTimeAlertStream
select apiName, apiVersion,  ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiCreatorTenantDomain, apiResourceTemplate, apiMethod, responseTime, thresholdResponseTime ,
    'Abnormal response time detected.' as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into AbnormalResponseTimeAlertStream;

-- Update the AlertCreatorConfig Table @ start
@info(name='Load ApiCreatorAlertConfigurationInMemoryTable')
from LoadInMemoryTable join ApiCreatorAlertConfiguration
on ApiCreatorAlertConfiguration.thresholdResponseTime > 0
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
update or insert into ApiCreatorAlertConfigurationInMemoryTable
set ApiCreatorAlertConfiguration.thresholdResponseTime = thresholdResponseTime
on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

-- Update AlertCreatorConfig table if receive event form APIM@info(name='Update ApiCreatorAlertConfigurationInMemoryTable')
@info(name='Update ApiCreatorAlertConfigurationInMemoryTable')
from ApiCreatorAlertConfigurationStream[thresholdResponseTime > 0]
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
update or insert into ApiCreatorAlertConfigurationInMemoryTable
set ApiCreatorAlertConfiguration.thresholdResponseTime = thresholdResponseTime
on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

@info(name='DeleteConfigurationif updated value is zero')
from ApiCreatorAlertConfigurationStream[thresholdResponseTime == 0]
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdResponseTime
delete  ApiCreatorAlertConfigurationInMemoryTable
 	on ApiCreatorAlertConfigurationInMemoryTable.apiName == apiName and ApiCreatorAlertConfigurationInMemoryTable.apiVersion == apiVersion and ApiCreatorAlertConfigurationInMemoryTable.apiCreator == apiCreator and ApiCreatorAlertConfigurationInMemoryTable.apiCreatorTenantDomain == apiCreatorTenantDomain;

@App:name("apim_alert_abnormal_backend_time_0")
@App:description("Identifies the API requests with abnormal backend time and add to AllAlertStream and AbnormalBackendTimeAlertStream")

define trigger LoadInMemoryTable at 'start';

@source(type = 'inMemory' , topic = 'APIM_REQUEST')
define stream Request (meta_clientType string, applicationConsumerKey string, applicationName string, applicationId string, applicationOwner string, apiContext string, apiName string, apiVersion string, apiResourcePath string, apiResourceTemplate string, apiMethod string, apiCreator string, apiCreatorTenantDomain string, apiTier string, apiHostname string, username string, userTenantDomain string, userIp string, userAgent string, requestTimestamp long, throttledOut bool, responseTime long, serviceTime long, backendTime long, responseCacheHit bool, responseSize long, protocol string, responseCode int, destination string, securityLatency long, throttlingLatency long, requestMedLat long, responseMedLat long, backendLatency long, otherLatency long, gatewayType string, label string);

@source(type = 'inMemory', topic = 'ApiCreatorAlertConfigurationStream' )
define stream ApiCreatorAlertConfigurationStream (
 apiName string,
 apiVersion string,
 apiCreator string,
 apiCreatorTenantDomain string,
 thresholdResponseTime long,
 thresholdBackendTime long);

define stream SuppressedAbnormalBackendTimeAlertStream(apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, apiResourceTemplate string, apiMethod string, backendTime long, thresholdBackendTime long);

@sink(type = 'inMemory' , topic = 'AbnormalBackendTimeAlertStream')
define stream AbnormalBackendTimeAlertStream( apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, apiResourceTemplate string, apiMethod string, backendTime long, thresholdBackendTime long, message string, severity int, alertTimestamp long);

@store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB')
@PrimaryKey('apiName', 'apiVersion', 'apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfiguration (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdResponseTime long, thresholdBackendTime long);

@PrimaryKey('apiName', 'apiVersion', 'apiCreator', 'apiCreatorTenantDomain')
define table ApiCreatorAlertConfigurationInMemoryTable (apiName string, apiVersion string, apiCreator string, apiCreatorTenantDomain string, thresholdBackendTime long);

-- Join the request stream with in memory configuration tables to get threashold values.
@info(name = 'creatorConfigurationInfoRetrieveQuery')
from Request as R join ApiCreatorAlertConfigurationInMemoryTable as C
 	on (R.apiName == C.apiName and R.apiVersion == C.apiVersion and R.apiCreator == C.apiCreator and R.apiCreatorTenantDomain == C.apiCreatorTenantDomain)
select R.apiName, R.apiVersion, R.apiCreator, R.apiCreatorTenantDomain, R.apiResourceTemplate, R.apiMethod, R.backendTime, C.thresholdBackendTime
insert into AbnormalBackendTimeAlertStreamTemp;

@info(name = 'FilterForAlerting')
from AbnormalBackendTimeAlertStreamTemp[ backendTime > thresholdBackendTime ]
select *
insert into AbnormalBackendTimeAlertStreamTemp2;

@info(name = 'Alert Suppression')
from AbnormalBackendTimeAlertStreamTemp2#window.length(1) as a left outer join SuppressedAbnormalBackendTimeAlertStream#window.time(${alertSuppressionTime} minute) as b
 	on (a.apiName == b.apiName and a.apiVersion == b.apiVersion and a.apiCreator == b.apiCreator and a.apiCreatorTenantDomain == b.apiCreatorTenantDomain and a.apiResourceTemplate == b.apiResourceTemplate and a.apiMethod == b.apiMethod)
select a.apiName, a.apiVersion, a.apiCreator, a.apiCreatorTenantDomain, a.apiResourceTemplate, a.apiMethod, a.backendTime, a.thresholdBackendTime
 	having b.apiName is null
insert into SuppressedAbnormalBackendTimeAlertStream;

@info(name = 'Alert Templated')
from SuppressedAbnormalBackendTimeAlertStream
select apiName, apiVersion, ifThenElse(apiCreatorTenantDomain == 'carbon.super', str:concat(apiCreator, "@carbon.super"), apiCreator) as apiCreator, apiCreatorTenantDomain, apiResourceTemplate, apiMethod, backendTime, thresholdBackendTime, 'Abnormal backend time detected.' as message, ${severityLevel} as severity, (time:timestampInMilliseconds()) as alertTimestamp
insert into AbnormalBackendTimeAlertStream;

-- Update the AlertCreatorConfig Table @ start
@info(name = 'Load ApiCreatorAlertConfigurationInMemoryTable')
from LoadInMemoryTable join ApiCreatorAlertConfiguration
 	on thresholdBackendTime > 0
select apiName, apiVersion, apiCreator, apiCreatorTenantDomain, thresholdBackendTime
update or
